function New-OpenFileDialog {
    
    <#
    
    .Description
        Creates a new Microsoft.Win32.OpenFileDialog
    .Synopsis
        Creates a new Microsoft.Win32.OpenFileDialog
    .Example
        New-OpenFileDialog
    .Example
        New-OpenFileDialog -OutputXaml
    .Parameter OutputXaml
        If Set, will output the object as XAML instead of creating it
    #>

    
    
    param(
        
    [Switch]
    ${Multiselect},

    [Switch]
    ${ReadOnlyChecked},

    [Switch]
    ${ShowReadOnly},

    [Switch]
    ${AddExtension},

    [Switch]
    ${CheckFileExists},

    [Switch]
    ${CheckPathExists},

    [System.String]
    ${DefaultExt},

    [Switch]
    ${DereferenceLinks},

    ${SafeFileNames},

    [System.String]
    ${FileName},

    ${FileNames},

    [System.String]
    ${Filter},

    [System.Int32]
    ${FilterIndex},

    [System.String]
    ${InitialDirectory},

    [Switch]
    ${RestoreDirectory},

    [System.String]
    ${Title},

    [Switch]
    ${ValidateNames},

    ${Tag},

    [System.Management.Automation.ScriptBlock[]]
    ${On_FileOk},

    [Switch]
    ${OutputXaml}
    )
    begin {
        
    }
    process {
        
        try {
        $Object = New-Object Microsoft.Win32.OpenFileDialog
        } catch {
            throw $_
            return
        } 
        Set-Property -property $psBoundParameters -inputObject $Object
        if ($outputXaml) {                
            $strWrite = New-Object IO.StringWriter
            ([xml]([Windows.Markup.XamlWriter]::Save($Object))).Save($strWrite)
            return "$strWrite"
        }
        $Object
    }
    end {
        
    }
